mapper.select:
		select
		  month_income
        from lk_user_wallet_month
        where
          wallet_year = #{walletYear}
          AND wallet_month = #{walletMonth}
          AND f_user_id = #{userId};

( select bill.bill_amount, day(bill.gmt_create) as bill_day, DAYOFWEEK(bill.gmt_create) as day_of_week, bill.gmt_create AS bill_date, bill.transaction_type, '支付宝' AS pay_method, vehicle.plate_num, `user`.real_name AS renter_name from bill, `order`, lk_space_day_renting_status AS day_rentings, vehicle, `user` where
bill.transaction_type = 1 AND year(bill.gmt_create) = #{walletYear} AND month(bill.gmt_create) = #{walletMonth}
AND `order`.f_actual_payment_bill_id = bill.id AND `order`.f_renting_status_id = day_rentings.id AND bill.to_account_id = ( select pay_ali.account_alipay_id from pay_ali where pay_ali.f_user_id = #{userId} ) AND day_rentings.f_vehicle_id = vehicle.id AND bill.from_user_id = `user`.id )
union all ( select bill.bill_amount, day(bill.gmt_create) as bill_day, DAYOFWEEK(bill.gmt_create) as day_of_week, bill.gmt_create AS bill_date, bill.transaction_type, '微信支付' AS pay_method, vehicle.plate_num, `user`.real_name AS renter_name from bill, `order`, lk_space_day_renting_status AS day_rentings, vehicle, `user` where
bill.transaction_type = 2 AND year(bill.gmt_create) = #{walletYear} AND month(bill.gmt_create) = #{walletMonth}
AND `order`.f_actual_payment_bill_id = bill.id AND `order`.f_renting_status_id = day_rentings.id AND bill.to_account_id = ( select pay_ali.account_alipay_id from pay_ali where pay_ali.f_user_id = #{userId} ) AND day_rentings.f_vehicle_id = vehicle.id AND bill.from_user_id = `user`.id
)
union all ( select bill.bill_amount, day(bill.gmt_create) as bill_day, DAYOFWEEK(bill.gmt_create) as day_of_week, bill.gmt_create AS bill_date, bill.transaction_type, pay_bank.bank_name AS pay_method, vehicle.plate_num, `user`.real_name AS renter_name from bill, `order`, lk_space_day_renting_status AS day_rentings, vehicle, `user`, pay_bank where
bill.transaction_type = 0 AND year(bill.gmt_create) = #{walletYear} AND month(bill.gmt_create) = #{walletMonth}
AND `order`.f_actual_payment_bill_id = bill.id AND `order`.f_renting_status_id = day_rentings.id AND bill.to_account_id = ( select pay_ali.account_alipay_id from pay_ali where pay_ali.f_user_id = #{userId} ) AND day_rentings.f_vehicle_id = vehicle.id AND bill.from_user_id = `user`.id AND pay_bank.f_user_id = bill.to_user_id )
order by bill_date
